$db = Factory::getContainer()->get( 'DatabaseDriver' );

$newUser          = new \stdClass();
$newUser->name    = 'John Doe';
$newUser->email   = 'john@doe.nl';
$newUser->created = date( 'Y-m-d H:i:s' );

try
{
    $db->insertObject( '#__users', $newUser );
}
catch ( Exception $e )
{
    echo $e->getMessage();
}
